mapper.insert:
		insert into 
			tbl_board (bno, title, content, writer)
    	values (
              (select nvl(max(bno)+1, 1) from tbl_board)
            , #{title }
            , #{content }
            , #{writer }
		)

mapper.update:
update tbl_board
set
	  writer = #{writer }
	, title = #{title }
	, content = #{content }
where bno = #{bno }
